mapper.select:
SELECT o.`player_id`, IFNULL(SUM(o.`pay_amount`), 0) AS `total_pay_amount` FROM `game_order` o WHERE o.`player_id` IN
#{playerId}
GROUP BY o.`player_id`

SELECT o.*, p.`nickname`, p.`account`, p.`sdk_channel`,
IFNULL(v.`id`, 0) AS `vip_id`, grg.`name` as `productName`
FROM `game_order` o
LEFT JOIN `game_player` p ON o.`player_id` = p.`player_id`
LEFT JOIN `game_vip` v ON o.`player_id` = v.`player_id`
LEFT JOIN `game_recharge_goods` grg on grg.`goods_id` = o.`product_id`

     ${configAuth } 


            AND o.`channel` = #{entity.channel}


            AND o.`server_id` = #{entity.serverId}


            AND o.`player_id` = #{entity.playerId}


            AND o.`order_id` LIKE CONCAT('%', #{entity.orderId}, '%')


            AND o.`query_id` LIKE CONCAT('%', #{entity.queryId}, '%')


            AND o.`product_id` = #{entity.productId}


            AND o.`order_status` = #{entity.orderStatus}



        AND o.`pay_amount` >= #{amountRange.min}
        AND o.`pay_amount` <= #{amountRange.max}


        AND o.`create_date` >= #{dateRange.start}
        AND o.`create_date` <= #{dateRange.end}


ORDER BY o.`create_time` DESC

SELECT IFNULL(SUM(o.`pay_amount`), 0) AS payAmount
FROM `game_order` o
LEFT JOIN `game_player` p ON o.`player_id` = p.`player_id`
WHERE o.`create_date` = DATE(#{payDate})
 ${configAuth } 
 AND p.`channel` = #{channel} 
 AND p.`sdk_channel` = #{sdkChannel} 
 AND o.`server_id` = #{serverId}

SELECT COUNT(DISTINCT o.`player_id`) AS num
FROM `game_order` o
LEFT JOIN `game_player` p ON o.`player_id` = p.`player_id`
WHERE o.`create_date` = DATE(#{payDate})
 ${configAuth } 
 AND p.`channel` = #{channel} 
 AND p.`sdk_channel` = #{sdkChannel} 
 AND o.`server_id` = #{serverId}

SELECT DATE(#{startDate}) AS `start_date`,
       DATE(#{endDate})   AS `end_date`,
       -- 活跃用户数
       (SELECT IFNULL(COUNT(DISTINCT(l.`player_id`)), 0)
        FROM `log_account` l LEFT JOIN `game_player` p ON l.`player_id` = p.`player_id`

            l.`type` = 2
             AND p.`channel` = #{channel} 
             AND l.`server_id` = #{serverId} 
             AND l.`create_date` >= DATE(#{startDate}) 
             AND l.`create_date` <= DATE(#{endDate}) 

        ) AS `active_num`,

        -- 付费用户数
        (SELECT IFNULL(COUNT(DISTINCT(o.`player_id`)), 0)
        FROM `game_order` o LEFT JOIN `game_player` p ON o.`player_id` = p.`player_id`

             AND p.`channel` = #{channel} 
             AND o.`server_id` = #{serverId} 
             AND o.`create_date` >= DATE(#{startDate}) 
             AND o.`create_date` <= DATE(#{endDate}) 

        ) AS `pay_num`,

        -- 付费金额
        (SELECT IFNULL(SUM(o.`pay_amount`), 0)
        FROM `game_order` o LEFT JOIN `game_player` p ON o.`player_id` = p.`player_id`

             AND p.`channel` = #{channel} 
             AND o.`server_id` = #{serverId} 
             AND o.`create_date` >= DATE(#{startDate}) 
             AND o.`create_date` <= DATE(#{endDate}) 

        ) AS `pay_amount`;

SELECT `server_id` AS serverId, SUM(`pay_amount`) AS payAmount FROM `game_order`
WHERE `order_status` = 4
 AND `create_time` >= #{startTime} 
 AND `create_time` <= #{endTime} 
GROUP BY `server_id`

SELECT o.`player_id`, p.`nickname`, p.`level`, p.`server_id`, p.`sid`, p.`channel`, p.`sdk_channel`, p.`create_time`, p.`login_time` AS `last_login_time`, MAX(o.`create_time`) AS `last_pay_time`, MAX(o.`id`) AS `order_id`, IFNULL(SUM(o.`pay_amount`), 0) AS `pay_amount`, IFNULL(v.`id`, 0) AS `vip_id` FROM `game_order` o LEFT JOIN `game_player` p ON o.`player_id` = p.`player_id` LEFT JOIN `game_vip` v ON o.`player_id` = v.`player_id`
${configAuth } AND p.`channel` = #{channel} AND p.`server_id` = #{serverId} AND p.`player_id` = #{playerId}
AND o.`create_date` >= #{dateRange.start} AND o.`create_date` <= #{dateRange.end}
GROUP BY o.`player_id` ORDER BY `pay_amount` DESC, o.`id` ASC
