mapper.select:
SELECT `ip`
FROM `log_account`
WHERE `player_id` = #{playerId}
  AND `create_date` = #{createDate}
ORDER BY `create_time` DESC LIMIT 1

SELECT a.`server_id` server_id, a.`player_id` player_id, a.`nickname` nickName, b.`type` `type`, b.`value` `value`, b.`create_date` create_date FROM `game_player` a INNER JOIN `log_player` b ON a.`player_id` = b.`player_id`
a.`server_id` = #{serverId}
AND a.`nickname` LIKE CONCAT('%', #{nickname},'%')
AND a.`player_id` = #{playerId}
AND b.`create_time` >= #{start} AND b.`create_time` <= #{end}

SELECT COUNT(DISTINCT a.`player_id`) AS counNum
FROM `log_account` a
WHERE a.`server_id` = #{serverId}
  AND a.`type` = #{type}
  AND a.`create_date` = DATE (#{createTime});

SELECT IFNULL(sum(a.`pay_amount`), 0) AS payAmount
FROM `game_order` a
         JOIN `log_account` b
              ON b.`player_id` = a.`player_id`
                  AND b.`server_id` = a.`server_id`
WHERE b.`server_id` = #{serverId}
  AND b.`type` = 1
  AND b.`create_date` = DATE (#{date})
  AND a.`server_id`= #{serverId}
  AND DATE (a.`pay_time`) = DATE (#{date});

SELECT count(DISTINCT a.`player_id`) AS payAmount
FROM `game_order` a
         JOIN `log_account` b
              ON b.`player_id` = a.`player_id`
                  AND b.`server_id` = a.`server_id`
WHERE b.`server_id` = #{serverId}
  AND b.`type` = 1
  AND b.`create_date` = DATE (#{date})
  AND a.`server_id`= #{serverId}
  AND DATE (a.`pay_time`) = DATE (#{date});

SELECT a.`player_id`
FROM `log_account` a
WHERE a.`server_id` = #{serverId}
  AND a.`type` = 2
  AND a.`create_date` = #{date}
group by a.`player_id`;

select.foreach:

    UNION ALL

SELECT a.`create_date`,
a.`player_id`
FROM `log_account` a
WHERE a.`type` = 2

    AND a.`server_id` = #{serverId}

AND a.`create_time` >= #{date.startTime}
AND a.`create_time` <= #{date.endTime}
GROUP BY a.`player_id`

mapper.select:

SELECT a.`player_id`
FROM `log_account` a
         LEFT JOIN (SELECT `player_id`
                    FROM `log_account`
                    WHERE `type` = 2
                      AND `server_id` = #{serverId}
                      AND `create_date` >= #{startDate}
                      AND `create_date` <= #{endDate}
                    GROUP BY `player_id`) t
                   ON t.`player_id` = a.`player_id`
WHERE a.`type` = 2
  AND a.`server_id` = #{serverId}
  AND t.`player_id` IS NULL
GROUP BY a.`player_id`;

SELECT `server_id` as serverId, COUNT(DISTINCT `player_id`) AS `num` FROM `log_account`
WHERE `type` = 2
AND `create_date` >= #{startTime}
AND `create_date` <= #{endTime}
GROUP BY `server_id`;
