mapper.select:
SELECT `ip`
FROM `log_account`
WHERE `player_id` = #{playerId}
  AND `create_date` = #{createDate}
ORDER BY `create_time` DESC LIMIT 1

SELECT p.`server_id` server_id, p.`player_id` player_id, p.`nickname` nickName, l.`type` `type`, l.`value` `value`, l.`create_date` create_date FROM `game_player` p INNER JOIN `log_player` l ON p.`player_id` = l.`player_id`
p.`server_id` = #{serverId}
AND p.`nickname` LIKE CONCAT('%', #{nickname},'%')
AND p.`player_id` = #{playerId}
AND l.`create_time` >= #{start} AND l.`create_time` <= #{end}

SELECT COUNT(DISTINCT l.`player_id`) AS counNum
FROM `log_account` l
WHERE l.`server_id` = #{serverId}
  AND l.`type` = #{type}
  AND l.`create_date` = DATE(#{date});

SELECT COUNT(DISTINCT l.`player_id`) AS counNum
FROM `log_account` l
LEFT JOIN `game_player` p ON l.`player_id` = p.`player_id`
WHERE l.`type` = #{type}
AND l.`create_date` = DATE(#{date})
 AND p.`channel` = #{channel} 
 AND p.`server_id` = #{serverId}

SELECT IFNULL(sum(o.`pay_amount`), 0) AS payAmount
FROM `game_order` o
    LEFT JOIN `game_player` p ON o.`player_id` = p.`player_id`
WHERE p.`server_id` = #{serverId}
  AND p.`create_date` = DATE(#{date})
  AND o.`server_id`= #{serverId}
  AND o.`create_date` = DATE(#{date});

SELECT count(DISTINCT o.`player_id`) AS payAmount
FROM `game_order` o
    JOIN `game_player` p ON o.`player_id` = p.`player_id`
WHERE p.`server_id` = #{serverId}
  AND p.`create_date` = DATE(#{date})
  AND o.`create_date` = DATE(#{date});

SELECT COUNT(*)
FROM (SELECT COUNT(o.`player_id`) AS num
FROM `game_order` o
LEFT JOIN `game_player` p ON o.`player_id` = p.`player_id`
WHERE p.`create_date` = DATE(#{date})
AND o.`create_date` = DATE(#{date})
 AND p.`channel` = #{channel} 
 AND p.`server_id` = #{serverId} 
GROUP BY o.`player_id`) c
WHERE c.`num` >= 2;

SELECT l.`player_id`
FROM `log_account` l
WHERE l.`server_id` = #{serverId}
  AND l.`type` = 2
  AND l.`create_date` = #{date}
GROUP BY l.`player_id`;

select.foreach:

    UNION ALL

SELECT l.`create_date`,
l.`player_id`
FROM `log_account` l
WHERE l.`type` = 2

    AND l.`server_id` = #{serverId}

AND l.`create_time` >= #{date.startTime}
AND l.`create_time` <= #{date.endTime}
GROUP BY l.`player_id`

mapper.select:
SELECT `server_id` as serverId, COUNT(DISTINCT `player_id`) AS `num` FROM `log_account`
WHERE `type` = 2
AND `create_date` >= #{startTime}
AND `create_date` <= #{endTime}
GROUP BY `server_id`;
