mapper.select:
SELECT l.`id`, l.`player_id`, p.`nickname`, p.`server_id`,
l.`value` AS `attr_type`,
l.`param_1` AS `before`,
l.`param_2` AS `delta`,
l.`param_3` AS `after`,
l.`create_time`
FROM `log_player` l
LEFT JOIN `game_player` p ON l.`player_id` = p.`player_id`

    l.`type` = 9

        AND p.`player_id` = #{playerId}

            AND p.`channel` = #{channel}
            AND p.`server_id` = #{serverId}


    AND l.`create_date` >= #{start}
    AND l.`create_date` <= #{end}

ORDER BY l.`create_time` DESC
