mapper.select:
SELECT
SUM(pay_amount)
FROM pay_order
WHERE
pay_time >= #{payTimeBeginDate} AND pay_time <= #{payTimeEndDate}
AND order_status > 0

    AND server_id = #{serverId}


    AND channel = #{channel}

SELECT
COUNT(id) pay_num_sum,
(COUNT(id) / (SELECT COUNT(*) FROM pay_order)) pay_num_sum_rate,
SUM(pay_amount) pay_amount_sum,
(SUM(pay_amount) / (SELECT SUM(pay_amount) FROM pay_order)) pay_amount_sum_rate,
(SUM(pay_amount) / COUNT(id)) arppu
FROM pay_order
WHERE
pay_time >= #{rangeDateBeginTime} AND pay_time <= #{rangeDateEndTime}
AND order_status > 0

    AND server_id = #{serverId}


    AND channel = #{channel}

AND order_amount >= #{payRankBegin} AND order_amount <= #{payRankEnd}
