mapper.select:
SELECT id, product_id, COUNT(product_id) AS product_count, COUNT(product_id) / #{productCount} AS product_count_ratio, SUM(pay_amount) AS pay_amount_sum, SUM(pay_amount)/ #{payAmountSum} AS pay_amount_ratio FROM pay_order WHERE pay_time >= #{payTimeBeginDate} AND pay_time <= #{payTimeEndDate} AND order_status > 0
AND server_id = #{serverId}
AND channel = #{channel}
GROUP BY product_id

SELECT
COUNT(*) product_count,
SUM(pay_amount) pay_amount_sum
FROM pay_order
WHERE
pay_time >= #{payTimeBeginDate} AND pay_time <= #{payTimeEndDate}
AND order_status > 0

    AND server_id = #{serverId}


    AND channel = #{channel}
