mapper.select:
SELECT po.`id`, po.`player_id`, pri.`name`, SUM(po.`pay_amount`) pay_amount_sum FROM `game_order` po LEFT JOIN `game_register_info` pri ON po.`player_id` = pri.`player_id` WHERE po.`pay_time` >= #{payTimeBeginDate} AND `pay_time` <= #{payTimeEndDate} AND po.`order_status` > 0
AND po.`server_id` = #{serverId}
AND po.`channel` = #{channel}
GROUP BY po.`player_id` ORDER BY pay_amount_sum DESC LIMIT 100

SELECT po.`id`, po.`channel`, po.`server_id`, pri.`account`, po.`player_id`, pri.`name`, SUM(po.`pay_amount`) pay_amount_sum, pri.`create_time`, MAX(po.`pay_time`) pay_time_max FROM `game_order` po LEFT JOIN `game_register_info` pri ON po.`player_id` = pri.`player_id` WHERE po.`pay_time` >= #{rangeDateBeginTime} AND `pay_time` <= #{rangeDateEndTime} AND po.`order_status` > 0
AND po.`server_id` = #{serverId}
AND po.`channel` = #{channel}
GROUP BY po.`player_id` ORDER BY pay_amount_sum DESC

SELECT a.`player_id` player_id, a.`create_time` register_time, MAX(b.`create_time`) last_login_time FROM `log_account` a LEFT JOIN `log_account` b ON a.`player_id` = b.`player_id` WHERE a.`type` = 1 AND b.`type` = 2
AND a.`server_id` = #{serverId}
GROUP BY a.`player_id`
