mapper.select:
SELECT
id,
account,
nickname,
level,
backpack_size,
practice_value,
practice_year,
combat_power,
create_time,
login_time,
login_ip
FROM
player
WHERE
id = #{playerId}
AND nickname = #{nickName}

    AND create_time >= #{createDateBegin}


    AND create_time <= #{createDateEnd}


    AND login_time >= #{loginDateBegin}


    AND login_time <= #{loginDateEnd}


    AND level >= #{levelBegin}


    AND level <= #{levelEnd}
